import React from 'react';

const CartItem = (props) => {
    
    
    
        const {price,title,qty} = props.product;
        const { product, onIncreaseQuantity,ondecreaseQuantity,onDeleteProduct} =props;
        return(
            <div className='cart-item'>
                <div className='left-block'>
                    <img style={styles.image} alt="not found" src={product.img}/>

                </div>
                <div className='right-block'>
                    <div style={{ fontSize: 25 }}>{title}</div>
                    <div style={{ color: '#777' }}>Rs {price}</div>
                    <div style={{ color: '#777' }}>Qty: {qty}</div>
                    <div className='cart-item-actions'>
                        {/* Buttons */}
                        <img alt="increase" className='action-icons' src='https://cdn-icons-png.flaticon.com/512/1828/1828926.png'  onClick={() => onIncreaseQuantity(product)}/>
                        <img alt="decrease" className='action-icons' src='https://cdn-icons-png.flaticon.com/512/992/992683.png'  onClick={() => ondecreaseQuantity(product)}/>
                        <img alt="delete" className='action-icons' src='https://cdn-icons-png.flaticon.com/512/3096/3096673.png'  onClick={() => onDeleteProduct(product.id)}/>
                    </div>


                </div>

            </div>

        );
    
}
const styles= {
    image: {
        height: 125,
        width: 125,
        borderRadius: 7,
        background: '#ccc'
    }
}
export default CartItem;
